		);
		$data['csvprice_pro_menu'][] = array(
			'url' => $this->url->link('csvprice_pro/app_product', 'token=' . $this->session->data['token'], true),
			'title' => $this->language->get('text_menu_product')
		);
		$data['csvprice_pro_menu'][] = array(
			'url' => $this->url->link('csvprice_pro/app_category', 'token=' . $this->session->data['token'], true),
			'title' => $this->language->get('text_menu_category')
		);
		$data['csvprice_pro_menu'][] = array(
			'url' => $this->url->link('csvprice_pro/app_manufacturer', 'token=' . $this->session->data['token'], true),
			'title' => $this->language->get('text_menu_manufacturer')
		);
		$data['csvprice_pro_menu'][] = array(
			'url' => $this->url->link('csvprice_pro/app_customer', 'token=' . $this->session->data['token'], true),
			'title' => $this->language->get('text_menu_customer')
		);
		$data['csvprice_pro_menu'][] = array(
			'url' => $this->url->link('csvprice_pro/app_order', 'token=' . $this->session->data['token'], true),
			'title' => $this->language->get('text_menu_order')
		);
